import React from "react";
import { Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import ReservarTurno from "./pages/ReservarTurno";

const button = document.querySelector('.reservar-turno-button');
let animationFrame; // Controla las actualizaciones para evitar exceso

button.addEventListener('mousemove', (event) => {
    if (animationFrame) cancelAnimationFrame(animationFrame); // Cancela actualizaciones pendientes

    animationFrame = requestAnimationFrame(() => {
        const rect = button.getBoundingClientRect();
        const x = event.clientX - rect.left; // Coordenada X relativa al bot贸n
        const y = event.clientY - rect.top;  // Coordenada Y relativa al bot贸n

        button.style.setProperty('--mouse-x', `${x}px`);
        button.style.setProperty('--mouse-y', `${y}px`);
    });
});


const AppRoutes = () => {
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/turnos" element={<ReservarTurno />} />
    </Routes>
  );
};

export default AppRoutes;
